import React, { useState } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faBars, faTimes } from "@fortawesome/free-solid-svg-icons";
import { faTwitter, faLinkedinIn } from "@fortawesome/free-brands-svg-icons";

const NavBar = (props) => {
  const [showDropdown, setShowDropdown] = useState(false);
  const [scrolled, setScrolled] = React.useState(false);

  useEffect(() => {
    window.addEventListener("scroll", handleScroll);
  });

  const handleScroll = () => {
    const offset = window.scrollY;
    if (offset > 200) {
      setScrolled(true);
    } else {
      setScrolled(false);
    }
  };

  const handleBurgerToggle = () => {
    setShowDropdown((showDropdown) => !showDropdown);
  };

  return (
    <div className={`Navbar ${showDropdown && "Navbar__Pop"} ${scrolled && "scrolled"}`}>
      <div className="Navbar__Responsive">
        <div className="Navbar__Link-brand">Osas Alohan</div>
        <div
          className="Navbar__Link Navbar__Link-toggle"
          onClick={handleBurgerToggle}
        >
          {showDropdown ? (
            <FontAwesomeIcon icon={faTimes} />
          ) : (
            <FontAwesomeIcon icon={faBars} />
          )}
        </div>
      </div>
      <nav
        className={`Navbar__Items Navbar__Items--right ${
          showDropdown && "Navbar__ToggleShow"
        }`}
      >
        <div className="Navbar__Link">Blog</div>
        <div className="Navbar__Link">Projects</div>
        <div className="Navbar__Link">Contact</div>
        <div className="Navbar__Link__Social">
          <div className="socials">
            <FontAwesomeIcon icon={faTwitter} />
          </div>
          <div className="socials">
            <FontAwesomeIcon icon={faLinkedinIn} />
          </div>
        </div>
      </nav>
    </div>
  );
};

export default NavBar;
